package edu.matc.controller;import edu.matc.entity.Store;import edu.matc.entity.User;import edu.matc.persistence.GenericServiceImpl;import edu.matc.persistence.HibernateUtil;import edu.matc.persistence.IGenericService;import javax.servlet.RequestDispatcher;import javax.servlet.ServletException;import javax.servlet.annotation.WebServlet;import javax.servlet.http.HttpServlet;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import java.io.IOException;import java.util.List;/** * Created on 11/13/17 at 5:57 PM * * @author nfenev */@WebServlet(        urlPatterns = {"/mySchedules"})public class MySchedules extends HttpServlet {    @Override    protected void doGet(HttpServletRequest req, HttpServletResponse resp) throws ServletException, IOException {        IGenericService<Store> storeIGenericService;        storeIGenericService = new GenericServiceImpl<>(                Store.class, HibernateUtil.getSessionFactory());        List<Store> listStores = storeIGenericService.getAll();        req.setAttribute("allStores", listStores);        RequestDispatcher dispatcher = req.getRequestDispatcher("/mySchedules.jsp");        dispatcher.forward(req, resp);    }}