package edu.matc.controller;import edu.matc.entity.Store;import edu.matc.persistence.GenericServiceImpl;import edu.matc.persistence.HibernateUtil;import edu.matc.persistence.IGenericService;import javax.servlet.RequestDispatcher;import javax.servlet.ServletException;import javax.servlet.annotation.WebServlet;import javax.servlet.http.HttpServlet;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import java.io.IOException;import java.util.HashMap;import java.util.List;import java.util.Map;/** * Created on 11/16/17 at 6:53 AM * * @author nfenev */@WebServlet(        urlPatterns = {"/storeShifts"})public class StoreShifts extends HttpServlet {    @Override    protected void doGet(HttpServletRequest req, HttpServletResponse resp) throws ServletException, IOException {        IGenericService<StoreShifts> storeIGenericService;        storeIGenericService = new GenericServiceImpl<>(                StoreShifts.class, HibernateUtil.getSessionFactory());        Map<String, Object> params = new HashMap<>();        String storeId = req.getParameter("storeId");        String hqlQuery = "from StoreShifts where storeId=" + storeId;        List<StoreShifts> listShifts = storeIGenericService.query(hqlQuery,params);        req.setAttribute("allShifts", listShifts);        RequestDispatcher dispatcher = req.getRequestDispatcher("/storeShifts.jsp");        dispatcher.forward(req, resp);    }}