package edu.matc.entity;import org.hibernate.annotations.GenericGenerator;import javax.persistence.*;/** * Created on 11/16/17 at 5:41 AM * * @author nfenev */@Entity@Table(name= "storeShifts")public class StoreShifts {    private int shiftsId;    private String workDay;    private String startTime;    private String endTime;    private String jobTitle;    private Store storeId;    public StoreShifts() {    }    public StoreShifts(int shiftsId, String workDay, String startTime, String endTime, String jobTitle, Store storeId) {        this.shiftsId = shiftsId;        this.workDay = workDay;        this.startTime = startTime;        this.endTime = endTime;        this.jobTitle = jobTitle;        this.storeId = storeId;    }    @Id    @GeneratedValue(generator = "increment")    @GenericGenerator(name = "increment", strategy = "increment")    @Column(name = "id")    public int getShiftsId() {        return shiftsId;    }    public void setShiftsId(int shiftsId) {        this.shiftsId = shiftsId;    }    @Column(name="work_day")    public String getWorkDay() {        return workDay;    }    public void setWorkDay(String workDay) {        this.workDay = workDay;    }    @Column(name= "start_time")    public String getStartTime() {        return startTime;    }    public void setStartTime(String openTime) {        this.startTime = openTime;    }    @Column(name= "end_time")    public String getEndTime() {        return endTime;    }    public void setEndTime(String closeTime) {        this.endTime = closeTime;    }    @Column(name="job_title")    public String getJobTitle() {        return jobTitle;    }    public void setJobTitle(String jobTitle) {        this.jobTitle = jobTitle;    }    @ManyToOne    @JoinColumn(name = "store_id", referencedColumnName = "id", nullable = false)    public Store getStoreId() {        return storeId;    }    public void setStoreId(Store storeId) {        this.storeId = storeId;    }}