package edu.matc.entity;import org.hibernate.annotations.GenericGenerator;import javax.persistence.*;/** * Created on 11/16/17 at 5:41 AM * * @author nfenev */@Entity@Table(name= "storeShifts")public class StoreShifts {    private int shiftsId;    private String workDay;    private String startTime;    private String endTime;    private String jobTitle;    private Store storeId;    /**     * Instantiates a new Store shifts.     */    public StoreShifts() {    }    /**     * Instantiates a new Store shifts.     *     * @param shiftsId  the shifts id     * @param workDay   the work day     * @param startTime the start time     * @param endTime   the end time     * @param jobTitle  the job title     * @param storeId   the store id     */    public StoreShifts(int shiftsId, String workDay, String startTime, String endTime, String jobTitle, Store storeId) {        this.shiftsId = shiftsId;        this.workDay = workDay;        this.startTime = startTime;        this.endTime = endTime;        this.jobTitle = jobTitle;        this.storeId = storeId;    }    /**     * Gets shifts id.     *     * @return the shifts id     */    @Id    @GeneratedValue(generator = "increment")    @GenericGenerator(name = "increment", strategy = "increment")    @Column(name = "id")    public int getShiftsId() {        return shiftsId;    }    /**     * Sets shifts id.     *     * @param shiftsId the shifts id     */    public void setShiftsId(int shiftsId) {        this.shiftsId = shiftsId;    }    /**     * Gets work day.     *     * @return the work day     */    @Column(name="work_day")    public String getWorkDay() {        return workDay;    }    /**     * Sets work day.     *     * @param workDay the work day     */    public void setWorkDay(String workDay) {        this.workDay = workDay;    }    /**     * Gets start time.     *     * @return the start time     */    @Column(name= "start_time")    public String getStartTime() {        return startTime;    }    /**     * Sets start time.     *     * @param openTime the open time     */    public void setStartTime(String openTime) {        this.startTime = openTime;    }    /**     * Gets end time.     *     * @return the end time     */    @Column(name= "end_time")    public String getEndTime() {        return endTime;    }    /**     * Sets end time.     *     * @param closeTime the close time     */    public void setEndTime(String closeTime) {        this.endTime = closeTime;    }    /**     * Gets job title.     *     * @return the job title     */    @Column(name="job_title")    public String getJobTitle() {        return jobTitle;    }    /**     * Sets job title.     *     * @param jobTitle the job title     */    public void setJobTitle(String jobTitle) {        this.jobTitle = jobTitle;    }    /**     * Gets store id.     *     * @return the store id     */    @ManyToOne    @JoinColumn(name = "store_id", referencedColumnName = "id", nullable = false)    public Store getStoreId() {        return storeId;    }    /**     * Sets store id.     *     * @param storeId the store id     */    public void setStoreId(Store storeId) {        this.storeId = storeId;    }}