package edu.matc.entity;import org.hibernate.annotations.Cascade;import javax.persistence.*;import java.io.Serializable;/** * Created on 11/16/17 at 4:22 AM * * @author nfenev */@Entity@Table(name="user_roles")public class UserRoles implements Serializable {    private String userName;    private String roleName;    private User userId;    public UserRoles() {    }    public UserRoles(String userName, String roleName, User userId) {        this.userName = userName;        this.roleName = roleName;        this.userId = userId;    }    @Id    @Column(name="user_name")    public String getUserName() {        return userName;    }    public void setUserName(String userName) {        this.userName = userName;    }    @Id    @Column(name="role_name")    public String getRoleName() {        return roleName;    }    public void setRoleName(String roleName) {        this.roleName = roleName;    }    @ManyToOne    @JoinColumn(name = "users_id", referencedColumnName = "id", nullable = false)    public User getUserId() {        return userId;    }    public void setUserId(User userId) {        this.userId = userId;    }}