package edu.matc.persistence;import edu.matc.entity.Store;import edu.matc.entity.StoreShifts;import edu.matc.entity.User;import org.junit.Before;import org.junit.Test;import java.util.ArrayList;import java.util.HashMap;import java.util.List;import java.util.Map;import static org.junit.Assert.*;/** * The type Generic service impl test. */public class GenericServiceImplTest {    /**     * The User service.     */    IGenericService<User> userService;    /**     * The Store shifts i generic service.     */    IGenericService<StoreShifts> storeShiftsIGenericService;    private User userDummy;    /**     * Sets up.     */    @Before    public void setUp() {        userService = new GenericServiceImpl<>(                User.class, HibernateUtil.getSessionFactory());        storeShiftsIGenericService = new GenericServiceImpl<>(                StoreShifts.class, HibernateUtil.getSessionFactory());        userDummy = new User();        userDummy.setFirstName("Nikolay");        userDummy.setLastName("Fenev");        userDummy.setUserName("niko");        userDummy.setUserPass("niko");        userDummy.setJobTitle("Bartender");        userDummy.setPerformance(3);    }    /**     * Gets all.     *     * @throws Exception the exception     */    @Test    public void getAll() throws Exception {        List<User> users = userService.getAll();        assertEquals("Number of users don't match", 2, users.size());    }    /**     * Test get user by id sucess.     */    @Test    public void testGetUserByIdSucess() {        userDummy.setUserid(2);        User thisUser = userService.get(User.class,2);        assertEquals("It is not the correct user", userDummy, thisUser);    }    /**     * Test save user success.     */    @Test    public void testSaveUserSuccess() {        userService.save(userDummy);        assertEquals("Did not insert the user", 3,userService.getAll().size() );    }    /**     * Test delete user success.     */    @Test    public void testDeleteUserSuccess() {        userService.delete(userDummy);        assertEquals("Did not delete user",3,userService.getAll().size());    }    /**     * Test update user success.     */    @Test    public void testUpdateUserSuccess() {        User thisUser = userService.get(User.class, 2);        thisUser.setJobTitle("CEO");        userService.update(thisUser);        assertEquals("The update failed","CEO",thisUser.getJobTitle());    }    /**     * Test query success.     */    @Test    public void testQuerySuccess() {        Map<String, Object> params = new HashMap<>();        String hqlQuery = "from StoreShifts where storeId=1";        List<StoreShifts> returnedShift= storeShiftsIGenericService.query(hqlQuery, params);        assertEquals("","Thirsty Buffalo", returnedShift.get(0).getStoreId().getStoreName());    }}